import React from "react";
import "./App.css";
import {
  Route,
  BrowserRouter as Router,
  Switch,
  Redirect,
} from "react-router-dom";
import { connect } from "react-redux";
import AuthRoute from "./router/AuthRoute";
import Login from "./modules/Login";
import PropTypes from "prop-types";
import LandingPage from "./modules/LandingPage";
import LoadingOverlay from "react-loading-overlay";

function App(props) {
  return (
    <div className="App">
      <DarkBackground disappear={true}>
        <LoadingOverlay active={true} spinner text="Loading your content...">
          <p>Some content or children or something.</p>
        </LoadingOverlay>
      </DarkBackground>

      <Router>
        <Switch>
          <Route exact={true} path="/login" component={LandingPage} />
          <AuthRoute isValidUser={true} />
          <Route
            path="/"
            render={() => <Redirect from="/" to="/login" />}
          ></Route>
          <Redirect from="*" to="/login" />
        </Switch>
      </Router>
    </div>
  );
}

const mapStateToProps = ({ isValidUser }) => {
  return { isValidUser };
};
export default connect(mapStateToProps, null)(App);

App.defaultProps = {
  isValidUser: false,
};
App.propTypes = {
  isValidUser: PropTypes.bool,
};
