import React, { useEffect, useState } from "react";
import styled from "styled-components";
import "./style.scss";
import { bindActionCreators } from "redux";
import {
  geStackExchanegeData,
  getListOfUsers,
  getTaskLists,
  createTask,
} from "./actions";
import NavBar from "../../components/NavBar";
import { authenticateUserAction } from "../Login/actions";
import { useDispatch, useSelector } from "react-redux";
import { ToastContainer, toast } from "react-toastify";
import DragDrop from "./DragDrop/index";
import DateTime from "../../components/DateTime";
import ModalWrapper from "../../components/ModalWrapper";
import Select from "../../components/Select";

const Container = styled.div`
  margin-top: 100px;
`;

const TopWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0% 10%;
  @media (max-width: 840px) {
    padding: 0% 5%;
  }
  @media (max-width: 720px) {
    display: block;
  }
`;

const Home = () => {
  const dispatch = useDispatch();
  const [modalVisble, setModalVisibility] = useState(true);
  const [completeJsonForDisplay, setCompleteJsonForDisplay] = useState({});

  const currentListOfUsers = useSelector(
    (state) => state?.homeData?.listOfAllUsers
  );
  let prioritySet = [
    { id: 1, name: "Low", value: "low" },
    { id: 2, name: "Medium", value: "medium" },
    { id: 3, name: "High", value: "high" },
  ];

  useEffect(() => {
    dispatch(getListOfUsers());
    dispatch(getTaskLists());
  }, []);

  const onChangeDateTime = (date) => {
    setCompleteJsonForDisplay({
      ...completeJsonForDisplay,
      due_date: date,
    });
  };

  const modalEventHandler = (type) => {
    if (type === "enable") return setModalVisibility(!modalVisble);
    else {
      const jsonCreation = {
        message: completeJsonForDisplay.message,
        due_date: completeJsonForDisplay.due_date,
        priority: completeJsonForDisplay.priority.id,
        assigned_to: completeJsonForDisplay.assigned_to.id,
      };
      if (
        completeJsonForDisplay.due_date &&
        Object.keys(completeJsonForDisplay.priority).length > 0 &&
        Object.keys(completeJsonForDisplay.assigned_to).length > 0 &&
        completeJsonForDisplay.message
      ) {
        dispatch(createTask(jsonCreation)).then((response) => {
          if (response?.data?.status === "success") {
            modalEventHandler("enable");
            setCompleteJsonForDisplay({});
            toastMessage("success", "Task successfully added.");
          }
        });
      } else {
        toastMessage("error", "Please fill all detail");
      }
    }
  };

  const onChangeMessage = (event) => {
    setCompleteJsonForDisplay({
      ...completeJsonForDisplay,
      message: event.target.value,
    });
  };

  const hadleOnChangePriority = (selectedOption, type) => {
    if (type === "priority") {
      setCompleteJsonForDisplay({
        ...completeJsonForDisplay,
        priority: selectedOption,
      });
    } else {
      setCompleteJsonForDisplay({
        ...completeJsonForDisplay,
        assigned_to: selectedOption,
      });
    }
  };

  const toastMessage = (toastType, message) => {
    toast[toastType](message, {
      position: "top-right",
      autoClose: 1000,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };
  return (
    <React.Fragment>
      <Container>
        {/* <DragDrop /> */}

        <button onClick={() => modalEventHandler("enable")}>Create Task</button>
        <DragDrop />
        {modalVisble && (
          <ModalWrapper
            modalEventHandler={() => modalEventHandler("enable")}
            modalVisble={modalVisble}
            outerClick={false}
            onSubmit={() => modalEventHandler("submit")}
            heading={"Create task"}
            btnName="Save"
          >
            <div>
              <div className="form-group">
                <label>Select Due-date :</label>
                <DateTime
                  className="form-control"
                  value={completeJsonForDisplay?.due_date}
                  onChange={onChangeDateTime}
                  showTime={true}
                  placeHolder="Please select Date and Time"
                />
              </div>
              <div className="form-group">
                <label>Message :</label>
                <input
                  className="form-control"
                  onChange={onChangeMessage}
                  value={completeJsonForDisplay?.message}
                />
              </div>
              <div className="form-group">
                <label>Priority :</label>
                <Select
                  currentSelected={completeJsonForDisplay?.priority}
                  handleOnChange={(event) =>
                    hadleOnChangePriority(event, "priority")
                  }
                  options={prioritySet}
                />
              </div>
              <div className="form-group">
                <label>Assigned :</label>
                <Select
                  currentSelected={completeJsonForDisplay?.assigned_to}
                  handleOnChange={(event) =>
                    hadleOnChangePriority(event, "assigned")
                  }
                  options={currentListOfUsers}
                />
              </div>
            </div>
          </ModalWrapper>
        )}
      </Container>
    </React.Fragment>
  );
  // }
};

export default Home;
