import React, { useEffect, useState } from "react";
import styled from "styled-components";
import "./style.css";
import { bindActionCreators } from "redux";
import { geStackExchanegeData, getListOfUsers, getTaskLists } from "./actions";
import NavBar from "../../components/NavBar";
import { authenticateUserAction } from "../Login/actions";
import { useDispatch, useSelector } from "react-redux";
import { ToastContainer, toast } from "react-toastify";
import DragDrop from "./DragDrop/index";
import DateTime from "../../components/DateTime";
import { requiredDateTimeFormat } from "../../constants/globalFunction";
import ModalWrapper from "../../components/ModalWrapper";
import Select from "react-select";

const Container = styled.div`
  margin-top: 100px;
`;

const TopWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0% 10%;
  @media (max-width: 840px) {
    padding: 0% 5%;
  }
  @media (max-width: 720px) {
    display: block;
  }
`;

const Home = () => {
  const dispatch = useDispatch();
  const [listUser, setListUser] = useState({});
  const [calenderValue, setCalenderValue] = useState(new Date());
  const [modalVisble, setModalVisibility] = useState(true);
  const [message, setMessage] = useState("");
  const [currentSelectedPriority, setCurrentSelectedPriority] = useState({});

  let prioritySet = [
    { id: 1, label: "Low", value: "low" },
    { id: 2, label: "Medium", value: "medium" },
    { id: 3, label: "High", value: "high" },
  ];

  useEffect(() => {
    dispatch(getListOfUsers());
    dispatch(getTaskLists());
  }, []);

  const onChangeDateTime = (date) => {
    setCalenderValue(date);
    console.log(requiredDateTimeFormat(date, false), "...dateConversion.....");
  };

  const modalEventHandler = () => {
    setModalVisibility(!modalVisble);
  };

  const onChangeMessage = (event) => {
    console.log(event.target.value);
    setMessage(event.target.value);
  };

  const toastMessage = (toastType, message) => {
    toast.dismiss();
    toast[toastType](message, {
      position: "top-right",
      autoClose: 1800,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };
  console.log(calenderValue, ".......calenderValue");
  return (
    <React.Fragment>
      <Container>
        {/* <DragDrop /> */}

        <button onClick={modalEventHandler}>Create Task</button>
        {modalVisble && (
          <ModalWrapper
            modalEventHandler={modalEventHandler}
            modalVisble={modalVisble}
          >
            <div>
              <div className="form-group">
                <label>Select Due-date :</label>
                <DateTime
                  value={calenderValue}
                  onChange={onChangeDateTime}
                  showTime={true}
                />
              </div>
              <div className="form-group">
                <label>Message :</label>
                <input
                  className="form-control"
                  onChange={onChangeMessage}
                  value={message}
                />
              </div>
            </div>
          </ModalWrapper>
        )}
      </Container>
    </React.Fragment>
  );
  // }
};

export default Home;
