import React from "react";
import "./App.css";
import {
  Route,
  BrowserRouter as Router,
  Switch,
  Redirect,
} from "react-router-dom";
import { connect } from "react-redux";
import AuthRoute from "./router/AuthRoute";
import Login from "./modules/Login";
import PropTypes from "prop-types";
import LandingPage from "./modules/LandingPage";
import LoadingOverlay from "react-loading-overlay";
const DarkBackground = styled.div`
  display: none; /* Hidden by default */
  position: fixed; /* Stay in place */
  z-index: 999; /* Sit on top */
  left: 0;
  top: 0;
  width: 100%; /* Full width */
  height: 100%; /* Full height */
  overflow: auto; /* Enable scroll if needed */
  background-color: rgb(0, 0, 0); /* Fallback color */
  background-color: rgba(0, 0, 0, 0.4); /* Black w/ opacity */

  ${(props) =>
    props.disappear &&
    css`
      display: block; /* show */
    `}
`;

function App(props) {
  return (
    <div className="App">
      <Router>
        <Switch>
          <Route exact={true} path="/login" component={LandingPage} />
          <AuthRoute isValidUser={true} />
          <Route
            path="/"
            render={() => <Redirect from="/" to="/login" />}
          ></Route>
          <Redirect from="*" to="/login" />
        </Switch>
        <DarkBackground disappear={false}>
          <LoadingOverlay active={true} spinner text="Loading your content...">
            <p>Some content or children or something.</p>
          </LoadingOverlay>
        </DarkBackground>
      </Router>
    </div>
  );
}

const mapStateToProps = ({ isValidUser }) => {
  return { isValidUser };
};
export default connect(mapStateToProps, null)(App);

App.defaultProps = {
  isValidUser: false,
};
App.propTypes = {
  isValidUser: PropTypes.bool,
};
