import React from "react";
import { toast } from "react-toastify";

const ReusableToast = ({ toastType, message }) => {
  const toastMessage = (toastType, message) => {
    toast[toastType](message, {
      position: "top-right",
      autoClose: 1000,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };
  return <div>{toastMessage()}</div>;
};

export default ReusableToast;
