import React, { Component } from "react";
import ReactDOM from "react-dom";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import * as Icon from "react-bootstrap-icons";
import { creatingRequiredDataFormat } from "../../constants/globalFunction";

// fake data generator
const getItems = (count, offset = 0) =>
  Array.from({ length: count }, (v, k) => k).map((k) => ({
    id: `item-${k + offset}`,
    content: `item ${k + offset}`,
  }));

// a little function to help us with reordering the result
const reorder = (list, startIndex, endIndex) => {
  const result = Array.from(list);
  const [removed] = result.splice(startIndex, 1);
  result.splice(endIndex, 0, removed);

  return result;
};

/**
 * Moves an item from one list to another list.
 */
const move = (
  source,
  destination,
  droppableSource,
  droppableDestination,
  findedNotAvailableName,
  findedNotAvailable
) => {
  let priorityTypeKey = "";
  if (droppableDestination.droppableId === "priorityLow") {
    priorityTypeKey = "1";
  } else if (droppableDestination.droppableId === "priorityMedium") {
    priorityTypeKey = "2";
  } else if (droppableDestination.droppableId === "priorityHigh") {
    priorityTypeKey = "3";
  }
  const sourceClone = Array.from(source);

  const destClone = Array.from(destination);

  debugger;
  // const newerDestinationWithNewerPriority = {
  //   ...destClone,
  //   priority: priorityTypeKey,
  // };
  const [removed] = sourceClone.splice(droppableSource.index, 1);
  this.props.handleDragDrop({
    ...removed,
    priority: priorityTypeKey,
    due_date: new Date(removed.due_date),
  });
  destClone.splice(droppableDestination.index, 0, {
    ...removed,
    priority: priorityTypeKey,
  });

  const result = {};
  result[droppableSource.droppableId] = sourceClone;
  result[droppableDestination.droppableId] = destClone;
  result[findedNotAvailableName] = findedNotAvailable;
  debugger;

  return result;
};

const grid = 8;

const getItemStyle = (isDragging, draggableStyle) => ({
  // some basic styles to make the items look a bit nicer
  userSelect: "none",
  padding: grid * 2,
  margin: `0 0 ${grid}px 0`,

  // change background colour if dragging
  background: isDragging ? "lightgreen" : "grey",

  // styles we need to apply on draggables
  ...draggableStyle,
});

const getListStyle = (isDraggingOver) => ({
  background: isDraggingOver ? "lightblue" : "lightgrey",
  padding: grid,
  width: 250,
});

class DragDrop extends Component {
  state = {
    items: [],
    selected: [],
    otherSelected: [],
    completeData: [],
  };
  static getDerivedStateFromProps(props, state) {
    console.log(state.completeData, "......state.....", props.listOfAllTasks);
    if (
      props.listOfAllTasks.length > 0 &&
      props.listOfAllTasks.length &&
      props.listOfAllTasks.length !== state.completeData.length
    ) {
      console.log(
        creatingRequiredDataFormat(props.listOfAllTasks),
        "tasks to display"
      );

      const filteredData = creatingRequiredDataFormat(props.listOfAllTasks);
      console.log(creatingRequiredDataFormat(props.listOfAllTasks));

      return {
        completeData: props.listOfAllTasks,
        items: filteredData.low,
        selected: filteredData.medium,
        otherSelected: filteredData.high,
      };
    } else return { ...state };
  }

  /**
   * A semi-generic way to handle multiple lists. Matches
   * the IDs of the droppable container to the names of the
   * source arrays stored in the state.
   */
  id2List = {
    priorityLow: "items",
    priorityMedium: "selected",
    priorityHigh: "otherSelected",
  };

  setOfAllData = ["priorityLow", "priorityMedium", "priorityHigh"];
  getList = (id) => this.state[this.id2List[id]];

  onDragEnd = (result) => {
    const { source, destination } = result;

    // dropped outside the list
    if (!destination) {
      return;
    }

    if (source.droppableId === destination.droppableId) {
      const items = reorder(
        this.getList(source.droppableId),
        source.index,
        destination.index
      );

      let state = { items };

      if (source.droppableId === "priorityMedium") {
        state = { selected: items };
      } else if (source.droppableId === "priorityHigh") {
        state = { otherSelected: items };
      }

      this.setState(state);
    } else {
      let setofCurrentSelected = [];
      setofCurrentSelected.push(source.droppableId);
      setofCurrentSelected.push(destination.droppableId);
      let findingNotAvaiable = this.setOfAllData.filter(
        (f) => !setofCurrentSelected.includes(f)
      );
      const result = move(
        this.getList(source.droppableId),
        this.getList(destination.droppableId),
        source,
        destination,
        findingNotAvaiable[0],
        this.state[this.id2List[findingNotAvaiable[0]]]
      );

      this.setState({
        items: result.priorityLow,
        selected: result.priorityMedium,
        otherSelected: result.priorityHigh,
      });
    }
  };

  // Normally you would want to split things out into separate components.
  // But in this example everything is just done in one place for simplicity
  render() {
    console.log(this.state, ".....stateprnting");
    return (
      <div className="d-flex">
        <DragDropContext onDragEnd={this.onDragEnd}>
          <Droppable droppableId="priorityLow">
            {(provided, snapshot) => (
              <div
                ref={provided.innerRef}
                style={getListStyle(snapshot.isDraggingOver)}
              >
                <div className="heading">Low Priority</div>
                {this.state.items.map((item, index) => (
                  <Draggable key={item.id} draggableId={item.id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={getItemStyle(
                          snapshot.isDragging,
                          provided.draggableProps.style
                        )}
                        className="draggable-component"
                      >
                        <div
                          className="cross-button"
                          onClick={() => this.props.handlingRemoval(item)}
                        >
                          <Icon.ArchiveFill color="red" size={20} />
                        </div>
                        {item.message}
                        {/* <div className="bottom-wrapper">
                          <div className="cross-button">
                            <Icon.ArrowUp color="green" size={20} />
                          </div>
                          <div
                            className="edit-button"
                            onClick={() => this.props.handlingEdit(item)}
                          >
                            <Icon.PencilSquare color="black" size={20} />
                          </div>
                        </div> */}
                        <div className="bottom-wrapper">
                          <div className="cross-button">
                            <Icon.ArrowUp color="#fd7e14" size={20} />
                          </div>
                          <div>
                            <div className="circular-name">
                              <span className="text-name">
                                {item.assigned_name}
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
          <Droppable droppableId="priorityMedium">
            {(provided, snapshot) => (
              <div
                ref={provided.innerRef}
                style={getListStyle(snapshot.isDraggingOver)}
              >
                <div className="heading">Medium Priority</div>

                {this.state.selected.map((item, index) => (
                  <Draggable key={item.id} draggableId={item.id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        className="draggable-component"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={getItemStyle(
                          snapshot.isDragging,
                          provided.draggableProps.style
                        )}
                      >
                        <div
                          className="cross-button"
                          onClick={() => this.props.handlingRemoval(item)}
                        >
                          <Icon.ArchiveFill color="red" size={20} />
                        </div>
                        {item.message}
                        <div className="bottom-wrapper">
                          <div className="cross-button">
                            <Icon.ArrowUp color="#fd7e14" size={20} />
                          </div>
                          <div>
                            <div className="circular-name">
                              <span className="text-name">
                                {item.assigned_name}
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          <Droppable droppableId="priorityHigh">
            {(provided, snapshot) => (
              <div
                ref={provided.innerRef}
                style={getListStyle(snapshot.isDraggingOver)}
              >
                <div className="heading">High Priority</div>
                {this.state.otherSelected.map((item, index) => (
                  <Draggable key={item.id} draggableId={item.id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        className="draggable-component"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={getItemStyle(
                          snapshot.isDragging,
                          provided.draggableProps.style
                        )}
                      >
                        <div
                          className="cross-button"
                          onClick={() => this.props.handlingRemoval(item)}
                        >
                          <Icon.ArchiveFill color="red" size={20} />
                        </div>
                        {item.message}

                        {/* <div
                            className="cross-button"
                            onClick={() => this.props.handlingEdit(item)}
                          >
                            <Icon.PencilSquare color="black" size={20} />
                          </div> */}
                        <div className="bottom-wrapper">
                          <div className="cross-button">
                            <Icon.ArrowUp color="#fd7e14" size={20} />
                          </div>
                          <div>
                            <div className="circular-name">
                              <span className="text-name">
                                {item.assigned_name}
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
      </div>
    );
  }
}
export default DragDrop;
