import React, { useEffect, useState } from "react";
import styled from "styled-components";
import "./style.css";
import { bindActionCreators } from "redux";
import { geStackExchanegeData, getListOfUsers, getTaskLists } from "./actions";
import NavBar from "../../components/NavBar";
import { authenticateUserAction } from "../Login/actions";
import { useDispatch, useSelector } from "react-redux";
import { ToastContainer, toast } from "react-toastify";
import DragDrop from "./DragDrop/index";
import DateTime from "../../components/DateTime";
import { requiredDateTimeFormat } from "../../constants/globalFunction";
import ModalWrapper from "../../components/ModalWrapper";
import Select from "../../components/Select";

const Container = styled.div`
  margin-top: 100px;
`;

const TopWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0% 10%;
  @media (max-width: 840px) {
    padding: 0% 5%;
  }
  @media (max-width: 720px) {
    display: block;
  }
`;

const Home = () => {
  const dispatch = useDispatch();
  const [listUser, setListUser] = useState({});
  const [calenderValue, setCalenderValue] = useState(new Date());
  const [modalVisble, setModalVisibility] = useState(true);
  const [message, setMessage] = useState("");
  const [currentSelectedPriority, setCurrentSelectedPriority] = useState({
    id: 2,
    name: "Medium",
    value: "medium",
  });
  const [assignedPerson, setAssignedPerson] = useState({});

  const currentListOfUsers = useSelector(
    (state) => state?.homeData?.listOfAllUsers
  );
  console.log(currentListOfUsers, ".....currentState");
  let prioritySet = [
    { id: 1, name: "Low", value: "low" },
    { id: 2, name: "Medium", value: "medium" },
    { id: 3, name: "High", value: "high" },
  ];

  useEffect(() => {
    dispatch(getListOfUsers());
    dispatch(getTaskLists());
  }, []);

  const onChangeDateTime = (date) => {
    setCalenderValue(date);
    console.log(requiredDateTimeFormat(date, false), "...dateConversion.....");
  };

  const modalEventHandler = () => {
    setModalVisibility(!modalVisble);
  };

  const onChangeMessage = (event) => {
    console.log(event.target.value);
    setMessage(event.target.value);
  };

  const hadleOnChangePriority = (selectedOption, type) => {
    console.log(type, ".....ty[pe");
    if (type === "priority") return setCurrentSelectedPriority(selectedOption);
    else return setAssignedPerson(selectedOption);
  };

  const toastMessage = (toastType, message) => {
    toast.dismiss();
    toast[toastType](message, {
      position: "top-right",
      autoClose: 1800,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };
  console.log(calenderValue, ".......calenderValue");
  return (
    <React.Fragment>
      <Container>
        {/* <DragDrop /> */}

        <button onClick={modalEventHandler}>Create Task</button>
        {modalVisble && (
          <ModalWrapper
            modalEventHandler={modalEventHandler}
            modalVisble={modalVisble}
          >
            <div>
              <div className="form-group">
                <label>Select Due-date :</label>
                <DateTime
                  className="form-control"
                  value={calenderValue}
                  onChange={onChangeDateTime}
                  showTime={true}
                />
              </div>
              <div className="form-group">
                <label>Message :</label>
                <input
                  className="form-control"
                  onChange={onChangeMessage}
                  value={message}
                />
              </div>
              <div className="form-group">
                <label>Priority :</label>
                <Select
                  currentSelected={currentSelectedPriority}
                  handleOnChange={(event) =>
                    hadleOnChangePriority(event, "priority")
                  }
                  options={prioritySet}
                />
              </div>
              <div className="form-group">
                <label>Assigned :</label>
                <Select
                  currentSelected={assignedPerson}
                  handleOnChange={(event) =>
                    hadleOnChangePriority(event, "assigned")
                  }
                  options={currentListOfUsers}
                />
              </div>
            </div>
          </ModalWrapper>
        )}
      </Container>
    </React.Fragment>
  );
  // }
};

export default Home;
