import React, { useEffect, useState } from "react";
import styled from "styled-components";
import "./style.css";
import { bindActionCreators } from "redux";
import { geStackExchanegeData, getListOfUsers, getTaskLists } from "./actions";
import NavBar from "../../components/NavBar";
import { authenticateUserAction } from "../Login/actions";
import { useDispatch, useSelector } from "react-redux";
import { ToastContainer, toast } from "react-toastify";
import DragDrop from "./DragDrop/index";
import DateTime from "../../components/DateTime";
import { requiredDateTimeFormat } from "../../constants/globalFunction";

const Container = styled.div`
  margin-top: 100px;
`;

const TopWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0% 10%;
  @media (max-width: 840px) {
    padding: 0% 5%;
  }
  @media (max-width: 720px) {
    display: block;
  }
`;

const Home = () => {
  const dispatch = useDispatch();
  const [listUser, setListUser] = useState({});
  const [calenderValue, setCalenderValue] = useState(new Date());

  useEffect(() => {
    dispatch(getListOfUsers());
    dispatch(getTaskLists());
  }, []);

  const onChangeDateTime = (date) => {
    setCalenderValue(date);
    console.log(requiredDateTimeFormat(date, false), "...dateConversion.....");
  };

  const toastMessage = (toastType, message) => {
    toast.dismiss();
    toast[toastType](message, {
      position: "top-right",
      autoClose: 1800,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };
  console.log(calenderValue, ".......calenderValue");
  return (
    <React.Fragment>
      <Container>
        {/* <DragDrop /> */}
        <DateTime
          value={calenderValue}
          onChange={onChangeDateTime}
          showTime={true}
        />
        <button
          type="button"
          class="btn btn-primary"
          data-toggle="modal"
          data-target="#exampleModalCenter"
        >
          Launch demo modal
        </button>
        <div
          class="modal fade"
          id="exampleModalCenter"
          tabindex="-1"
          role="dialog"
          aria-labelledby="exampleModalCenterTitle"
          aria-hidden="true"
        >
          <div class="modal-dialog modal-dialog-centered" role="document">
            <div class="modal-content">
              <div class="modal-header">
                <h5 class="modal-title" id="exampleModalCenterTitle">
                  Modal title
                </h5>
                <button
                  type="button"
                  class="close"
                  data-dismiss="modal"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div class="modal-body">...</div>
              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-secondary"
                  data-dismiss="modal"
                >
                  Close
                </button>
                <button type="button" class="btn btn-primary">
                  Save changes
                </button>
              </div>
            </div>
          </div>
        </div>
        32 bootstrap modal
      </Container>
    </React.Fragment>
  );
  // }
};

export default Home;
