import React, { useState } from "react";
import "./style.scss";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

function ModalWrapper({ modalVisble, modalEventHandler, children }) {
  return (
    <>
      <Button variant="primary" onClick={modalEventHandler}>
        Launch demo modal
      </Button>
      <Modal show={modalVisble} onHide={modalEventHandler}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        {children}
        <Modal.Footer>
          <Button variant="secondary" onClick={modalEventHandler}>
            Close
          </Button>
          <Button variant="primary" onClick={modalEventHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ModalWrapper;

ModalWrapper.defaultProps = {};
