import React, { useEffect, useState } from "react";
import styled from "styled-components";
import "./style.scss";
import { bindActionCreators } from "redux";
import {
  geStackExchanegeData,
  getListOfUsers,
  getTaskLists,
  createTask,
} from "./actions";
import NavBar from "../../components/NavBar";
import { authenticateUserAction } from "../Login/actions";
import { useDispatch, useSelector } from "react-redux";
import { ToastContainer, toast } from "react-toastify";
import DragDrop from "./DragDrop/index";
import DateTime from "../../components/DateTime";
import { requiredDateTimeFormat } from "../../constants/globalFunction";
import ModalWrapper from "../../components/ModalWrapper";
import Select from "../../components/Select";

const Container = styled.div`
  margin-top: 100px;
`;

const TopWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0% 10%;
  @media (max-width: 840px) {
    padding: 0% 5%;
  }
  @media (max-width: 720px) {
    display: block;
  }
`;

const Home = () => {
  const dispatch = useDispatch();
  const [listUser, setListUser] = useState({});
  const [calenderValue, setCalenderValue] = useState();
  const [requiredDateFormat, setRequiredDateFormat] = useState();
  const [modalVisble, setModalVisibility] = useState(true);
  const [message, setMessage] = useState("");
  const [currentSelectedPriority, setCurrentSelectedPriority] = useState({});
  const [assignedPerson, setAssignedPerson] = useState({});

  const [completeJsonForDisplay, setCompleteJsonForDisplay] = useState({});

  const currentListOfUsers = useSelector(
    (state) => state?.homeData?.listOfAllUsers
  );
  console.log(currentListOfUsers, ".....currentState");
  let prioritySet = [
    { id: 1, name: "Low", value: "low" },
    { id: 2, name: "Medium", value: "medium" },
    { id: 3, name: "High", value: "high" },
  ];

  useEffect(() => {
    dispatch(getListOfUsers());
    dispatch(getTaskLists());
  }, []);

  const onChangeDateTime = (date) => {
    const geetingValidDate = requiredDateTimeFormat(date, false);
    setCalenderValue(date);
    setCompleteJsonForDisplay({
      ...completeJsonForDisplay,
      due_date: geetingValidDate,
    });
    setRequiredDateFormat(geetingValidDate);
    console.log(geetingValidDate, "...dateConversion.....");
  };

  const modalEventHandler = (type) => {
    if (type === "enable") return setModalVisibility(!modalVisble);
    else {
      const jsonCreation = {
        message,
        due_date: requiredDateFormat,
        priority: currentSelectedPriority.id,
        assigned_to: assignedPerson.id,
      };
      if (
        calenderValue &&
        Object.keys(currentSelectedPriority).length > 0 &&
        Object.keys(assignedPerson).length > 0 &&
        message
      ) {
        dispatch(createTask(jsonCreation)).then((response) => {
          if (response?.data?.status === "success") {
            modalEventHandler("enable");
            toastMessage("success", "Task successfully added.");
          }
        });
      } else {
        toastMessage("error", "Please fill all detail");
      }
    }
  };

  const onChangeMessage = (event) => {
    console.log(event.target.value);
    setMessage(event.target.value);
    setCompleteJsonForDisplay({
      ...completeJsonForDisplay,
      message: event.target.value,
    });
  };

  const hadleOnChangePriority = (selectedOption, type) => {
    console.log(type, ".....ty[pe");
    if (type === "priority") {
      setCurrentSelectedPriority(selectedOption);
      setCompleteJsonForDisplay({
        ...completeJsonForDisplay,
        priority: selectedOption,
      });
    } else {
      setAssignedPerson(selectedOption);
      setCompleteJsonForDisplay({
        ...completeJsonForDisplay,
        assigned_to: selectedOption,
      });
    }
  };

  const toastMessage = (toastType, message) => {
    toast[toastType](message, {
      position: "top-right",
      autoClose: 1000,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };
  console.log(calenderValue, ".......calenderValue");
  return (
    <React.Fragment>
      <Container>
        {/* <DragDrop /> */}

        <button onClick={() => modalEventHandler("enable")}>Create Task</button>
        {modalVisble && (
          <ModalWrapper
            modalEventHandler={() => modalEventHandler("enable")}
            modalVisble={modalVisble}
            outerClick={false}
            onSubmit={() => modalEventHandler("submit")}
            heading={"Create task"}
            btnName="Save"
          >
            <div>
              <div className="form-group">
                <label>Select Due-date :</label>
                <DateTime
                  className="form-control"
                  value={calenderValue}
                  onChange={onChangeDateTime}
                  showTime={true}
                  placeHolder="Please select Date and Time"
                />
              </div>
              <div className="form-group">
                <label>Message :</label>
                <input
                  className="form-control"
                  onChange={onChangeMessage}
                  value={message}
                />
              </div>
              <div className="form-group">
                <label>Priority :</label>
                <Select
                  currentSelected={currentSelectedPriority}
                  handleOnChange={(event) =>
                    hadleOnChangePriority(event, "priority")
                  }
                  options={prioritySet}
                />
              </div>
              <div className="form-group">
                <label>Assigned :</label>
                <Select
                  currentSelected={assignedPerson}
                  handleOnChange={(event) =>
                    hadleOnChangePriority(event, "assigned")
                  }
                  options={currentListOfUsers}
                />
              </div>
            </div>
          </ModalWrapper>
        )}
      </Container>
    </React.Fragment>
  );
  // }
};

export default Home;
